function searchAnswers() {
    const query = document.getElementById('search').value.toLowerCase().trim().replace(/\s+/g, ''); // Удаляем лишние пробелы
    const results = document.getElementById('results');
    results.innerHTML = '';

    // Проверяем, введен ли текст в поле ввода
    if (query.length === 0) {
        return; // Не отображаем ничего, если поле пустое
    }

    const filteredAnswers = answers.filter(item => {
        const questionWithoutSpaces = item.question.toLowerCase().replace(/\s+/g, ''); // Удаляем пробелы из вопроса
        return questionWithoutSpaces.includes(query); // Проверяем, содержится ли запрос в вопросе
    });

    filteredAnswers.forEach(item => {
        const li = document.createElement('li');
        li.innerHTML = `${item.answer}`; // Используем обратные кавычки для вставки значения
        results.appendChild(li);
    });
}

function clearSearch() {
    document.getElementById('search').value = '';
    document.getElementById('results').innerHTML = ''; // Очищаем результаты
}
